import './App.css';
import React, { useState, useReducer } from 'react';
import A from './Components/Context_API/A';
import { consumer, Provider, provider, userContext } from './Components/Context_API/UserContext';
import D from './Components/Context_API/D';
import B from './Components/Context_API/B';
import Counter from './Components/useReducer/Counter';
import Counter_Object from './Components/useReducer/Counter_Object';


// Step 2 : Create context
export const CounterContext = React.createContext()


const intialState = 0;
const reducer = (currState, action) => {
  switch (action) {
    case 'increment':
      return currState + 1;
    case 'decrement':
      return currState - 1;
    case 'reset':
      return intialState
  }
}


function App() {

  const [newState, dispatch] = useReducer(reducer, intialState)
  return (
    < CounterContext.Provider value={{ counter: newState, counterDispatch: dispatch }}>
      <p>{newState}</p>
      <A />
      <B />
      <C />
    </CounterContext.Provider >
  )
}

export default App;


