import React, { Component } from 'react'

export class MouseListener extends Component {
    constructor() {
        super();
        this.state = {
            X: 0,
            Y: 0
        }
        //binding the method to make it pointing towards the current class
        this.logMouseMovement = this.logMouseMovement.bind(this)
    }
    //once the component is mounted then execute the folloeing 
    componentDidMount() {
        console.log("component did mount")
        window.addEventListener('mousemove', () => this.logMouseMovement)
    }
    logMouseMovement(event) {
        this.setState({ X: event.clientX, Y: event.clientY })
    }
    render() {
        return (
            <div>
                <p>the mouse position is ${this.state.X} and ${this.state.Y}</p>
            </div>
        )
    }
}

export default MouseListener
