import React, { useState } from 'react'

function Input() {
    const [name, setName] = useState("");
    const [mobileNumber, setMobileNumber] = useState('');
    const [gender, setGender] = useState("")
    const handleMobileNumber = (event) => {
        const pattern = "/^[7-9]\d{9}$/";
        const mobile = event.target.value
        if (regex.match(pattern, mobile)) {
            setMobileNumber(mobile)
        }
        else {
            alert("please enter proper mobile Number")
        }
    }
    return (
        <div>
            <input type="text" value={name} onChange={event => setName(event.target.value)} />
            <input type="number" value={mobileNumber} onChange={handleMobileNumber} />
            <select value={gender} onChange={event => event.target.value}>
                <option value="female">Female</option>
                <option value="male">Male</option>
            </select>
        </div>
    )
}

export default Input
